import {create} from 'zustand'
import io from "socket.io-client";
import {axiosInstance} from "../lib/axios.js";
import toast from "react-hot-toast";
const BASE_URL = 'http://localhost:5001'
const socket = io(BASE_URL);
export const useAuthStore = create((set,get)=>({
    authUser:null,
    isCheckingAuth: true,
    checkAuth: async () =>{
        try {
            const res = await axiosInstance.get("auth/check");
            if(res.status === 200){
                set({authUser:res.data})
            }
            else {
                set({authUser:null})
                throw new Error("no auth")
            }
        }
        catch (e){
            console.log('error in Check Auth',e.message)
            set({authUser:null})
            throw new Error("no auth")
        }
        finally {
            set({isCheckingAuth:false})
        }
    },
    signUpError:null,
    isSigningUp:false,
    signUp:async (data)=>{
        try {
            set({isSigningUp:true})
            const res = await axiosInstance.post("auth/signup",data);
            if(res.status === 201) {
                set({authUser: res.data})
                set({signUpError:null})
                sessionStorage.setItem('token',res.data.token)
                toast.success("account created")
            }
            else {
                set({authUser:null})
                set({signUpError:res.data.message})

            }
        }
        catch (e) {
            toast(e.response.data.message)

        }
        finally {
            set({isSigningUp:false})

        }
    }
    ,
    logIn:async (data)=>{
        try{
            set({isLoggingIn:true})
             const res = await axiosInstance.post('auth/login',data)
            console.log(res.data,'auth user from login')
                set({authUser:res.data})
                get().connectSocket()
                sessionStorage.setItem('token',res.data.token)

            toast.success("logged In")
        }
        catch (e){
            toast.error(e.message)
        }
        finally {
            set({isLoggingIn:false})
        }
    },
    logOut:async ()=>{
        try{
            sessionStorage.clear()
            set({authUser:null})
            get().disconnectSocket()
            toast.success("logged out")
        }
        catch (e){
            console.log(e)
            toast.error(e.response.data.message)
        }

    },
    updateProfile:async (data)=>{
        try {
            await useAuthStore.getState().checkAuth()
            await axiosInstance.put("auth/update-profile",data)
            toast.success("profile Image updated")
            set({authUser:{profilePic:data.profilePic}})
        }
        catch (e){
            toast.error(e.response.data.message)
        }
    },
    connectSocket: ()=>{

        const {authUser} = get();
        console.log(socket.connected,authUser)
        if(!socket.connected && authUser){
            socket.connect()
            console.log("connectionecting to socket")
        }
    },
    disconnectSocket:()=>{
        if(socket.connected){
            socket.disconnect()
        }
    }
}));

